import styles from "./ActualizarDatos.module.css"
import Campo from "../../Components/Campo"
import ListaOpciones from "../../Components/ListaOpciones"
import Boton from "../../Components/Boton"
import {  AiFillCloseCircle} from "react-icons/ai"
 

const ActualizarDatos = (props) => {
    const { className, closeModal } = props
    

    return (
        <>
            <div className={className}>
                <div>
                    <section className={styles.header}>
                        <h1 className={styles.titulo}>EDITAR CARD:</h1>
                        <AiFillCloseCircle className={styles.cerrar} onClick={closeModal} />
                    </section>
                    
                    
                    <section className={styles.contenedor1}>
                        <Campo
                        titulo="Titulo"
                        placeholder="Ingrese el titulo"
                        required
                        //valor={nombre}
                        //actualizarValor={actualizarNombre}
                        />
                        <ListaOpciones
                        //valor={equipos}
                        //actualizarEquipo={actualizarEquipo}
                        //equipos={props.equipos}
                        />
                    </section>
                    <section className={styles.contenedor1}>
                        <Campo
                        titulo="Imagen"
                        placeholder="Ingrese el enlace de la Imagen"
                        required
                        //valor={nombre}
                        //actualizarValor={actualizarNombre}
                        />
                        <Campo
                        titulo="Video"
                        placeholder="Ingrese el enlace del Video"
                        required
                        //valor={nombre}
                        //actualizarValor={actualizarNombre}
                        />
                    </section>
                    <section className={styles.contenedor2}>
                    <Campo
                        titulo="Descripcion"
                        placeholder="Ingrese la descripcion del video"
                        required
                        //valor={nombre}
                            //actualizarValor={actualizarNombre}
                        />
                    </section>
                    <div className={styles.botones}>
                        <Boton >Guardar</Boton>
                        <Boton >Limpiar</Boton>
                        </div>
                </div>
            </div>
        </>
    )
}

export default ActualizarDatos
