import styles from "./Formulario.module.css"
import Campo from "../../Components/Campo"
import ListaOpciones from "../../Components/ListaOpciones"
import Boton from "../../Components/Boton"
import { useState } from "react"


function Categorias() {
    const [nombre, actualizarNombre] = useState("")
    const [puesto, actualizarPuesto] = useState("")
    const [foto, actualizarFoto] = useState("")
    const [equipo, actualizarEquipo] = useState("")

    const [titulo, actualizarTitulo] = useState("")
    const [color, actualizarColor] = useState("")

    const { registrarColaborador, crearEquipo } = props

    const manejarEnvio = (e) => {
        e.preventDefault()
        console.log("Manejar el envio")
        let datosAEnviar = {
            nombre,
            puesto,
            foto,
            equipo
        }
        registrarColaborador(datosAEnviar)
    }

    const manejarNuevoEquipo = (e) => {
        e.preventDefault()
        crearEquipo({ titulo, colorPrimario: color })
    }





    return (
        <>
            <div className={styles.contenedor}>
                <h1 className={styles.titulo}>NUEVO VIDEO</h1>
                <p >COMPLETE EL FORMULARIO PARA CREAR UNA NUEVA TARJETA DE VIDEO</p>
                <hr/>
                <h2 className={styles.titulo2}>Crear Tarjeta</h2>
                <hr/>
                
                <section className={styles.contenedor1}>
                    <Campo
                    titulo="Titulo"
                    placeholder="Ingrese el titulo"
                    required
                    valor={nombre}
                    actualizarValor={actualizarNombre}
                    />
                    <ListaOpciones
                    valor={equipos}
                    actualizarEquipo={actualizarEquipo}
                    equipos={props.equipos}
                    />
                </section>
                <section className={styles.contenedor1}>
                    <Campo
                    titulo="Imagen"
                    placeholder="Ingrese el enlace de la Imagen"
                    required
                    valor={nombre}
                    actualizarValor={actualizarNombre}
                    />
                    <Campo
                    titulo="Video"
                    placeholder="Ingrese el enlace del Video"
                    required
                    valor={nombre}
                    actualizarValor={actualizarNombre}
                    />
                </section>
                <section className={styles.contenedor2}>
                <Campo
                    titulo="Descripcion"
                    placeholder="Ingrese la descripcion del video"
                    required
                    valor={nombre}
                actualizarValor={actualizarNombre}
                    />
                </section>
                <div className={styles.botones}>
                    <Boton onClick={manejarEnvio} >Guardar</Boton>
                    <Boton >Limpiar</Boton>
                </div>
            </div>
        </>
    )

}

export default Categorias
